import express from "express";
import { signInAuth } from "../middleware/signInAuth.js";
import { signUpController, signInController, getUsersController, getUserByIdController, deleteUserController, patchUserController, refreshTokenController, logoutController, forgotPasswordController, resetPasswordController, } from "../controllers/userController.js";
import { authorizationMiddleware } from "../middleware/auth.js";
import { schemas, validateRequest } from "../middleware/validator.js";
const router = express.Router();
router.post("/auth/signup", validateRequest(schemas.signup), signUpController);
router.post("/auth/signin", validateRequest(schemas.signin), signInAuth, signInController);
router.get("/auth/refresh", refreshTokenController);
router.post("/auth/forgot-password", validateRequest(schemas.forgotPassword), forgotPasswordController);
router.post("/auth/reset-password/:token", resetPasswordController);
router.use(authorizationMiddleware);
router.get("/", getUsersController);
router.get("/logout", logoutController);
router.get("/:id", getUserByIdController);
router.delete("/:id", deleteUserController);
router.patch("/:id", validateRequest(schemas.patchUser), patchUserController);
export default router;
